<?phpclass Cybertech_Contact_IndexController extends Mage_Core_Controller_Front_Action{    public function indexAction()    {        //Get current layout state        $this->loadLayout();          $this->getLayout()->getBlock('root')->setTemplate('page/2columns-left.phtml');        $this->getLayout()->getBlock('head')->setTitle($this->__('Contact'));                $breadcrumbs = $this -> getLayout() -> getBlock("breadcrumbs");        $breadcrumbs -> addCrumb("home", array(                "label" => $this -> __("Home"),                 "title" => $this -> __("Home"),                 "link" => Mage::getBaseUrl()            )        );        $breadcrumbs -> addCrumb("contact",             array(                "label" => $this -> __("Contact"),                 "title" => $this -> __("Contact")            )        );                $block = $this->getLayout()->createBlock(            'Mage_Core_Block_Template',            'cybertech.contact',            array(                'template' => 'contact-custom/contact.phtml'            )        );
        $this->getLayout()->getBlock('content')->append($block);        //$this->getLayout()->getBlock('right')->insert($block, 'catalog.compare.sidebar', true);        $this->_initLayoutMessages('core/session');        $this->renderLayout();    }}
 
